#include<iostream>
#include<set>
#include<string>
#include<sstream>

using namespace std;

set<string> perm;

 void genPerm(string& s1) {
        int count = 0;
        int n = s1.size();
        const char* r = s1.c_str();
        int j = 0;
        int s = 0;
        int q = 0;
        
        char a[n+1];
        char curr; 
        
        int c[n+1];
        int o[n+1];
        for (int i = 1; i<=n; i++) {
            c[i] = 0;
            o[i] = 1;
            a[i-1] = r[i-1];
        }
        a[n] = '\0';

    P2: //vistit permu a[1..n]
        perm.insert(string(a));
        //cout<<string(a)<<endl;
        count++;
    
    P3: // preapre for change
        j = n;
        s = 0;
    P4: // Ready for change?
        q = c[j]+o[j];
        if (q < 0) {
            goto P7;
        }
        
        if (q==j) {
            goto P6;
        }
    P5: // change
        curr = a[j-c[j]+s-1];
        a[j-c[j]+s-1] = a[j-q+s-1];
        a[j-q+s-1] = curr;
        c[j] = q;
        goto P2;
    P6: // Increase s
        if (j==1) {
            cout<<count<<endl;
            return;
        }
        
        s = s+1;
        
    P7: // switch direction
        o[j] = o[j]*(-1);
        j = j -1;
        goto P4;
}



int main()
{
    string s1("prosperity");
    //string s1("asdfghjklzxcvbn");
    //string s1("asdfghjkkk");
    genPerm(s1);
}